import * as React from 'react'
import InputLabel from '@mui/material/InputLabel'
import MenuItem from '@mui/material/MenuItem'
import FormHelperText from '@mui/material/FormHelperText'
import FormControl from '@mui/material/FormControl'
import Select from '@mui/material/Select'
import { useFormik } from 'formik'
import * as yup from 'yup';
import axios from 'axios';
import { useState } from 'react'
const router = useRouter();

export default function Home() {
    const [kecamatan, setKecamatan] = React.useState('')
    const [error, setError] = useState(null)
    const [success, setSuccess] = useState(null)

    const handleChange = (event) => {
      setKecamatan(event.target.value)
      router.push(`/${e.target.value}`)
    }

    const handleLoadValue = async (kecamatan) => {
        const result = await axios
        .get(`http://localhost:5000/home/'${kecamatan}'`)
        .catch((err) =>{
            if(err && err.response) {
                setError(err)
                setSuccess(null)
            }
        })
        if(result) {
            setError(null);
            return JSON.stringify(result)
        }
        console.log(result)
    }

    return (
      <div>
        <FormControl sx={{ m: 1, minWidth: 120 }}>
          <InputLabel id="demo-simple-select-helper-label">Kecamatan</InputLabel>
          <Select
            labelId="demo-simple-select-helper-label"
            id="demo-simple-select-helper"
            value={kecamatan}
            label="Kecamatan"
            onChange={handleChange}
          >
            <MenuItem value="">
              <em>---Pilih---</em>
            </MenuItem>
            <MenuItem value={'kec-telanaipura'} onClick={handleChange} >Kec. Telanaipura</MenuItem>
            <MenuItem value={'kec-jambiselatan'} onClick={handleChange} >Kec. Jambi Selatan</MenuItem>
            <MenuItem value={'kec-jambitimur'} onClick={handleChange} >Kec. Jambi Timur</MenuItem>
          </Select>
          <FormHelperText>Pilih Kecamatan</FormHelperText>
        </FormControl>
        <FormControl sx={{ m: 1, minWidth: 200 }}>
          {handleLoadValue.result}
        </FormControl>
      </div>
    )
}
