import * as React from 'react'
import axios from 'axios';
import { useState } from 'react'
import { useFormik } from 'formik'
import * as yup from 'yup';
import InputLabel from '@mui/material/InputLabel'
import MenuItem from '@mui/material/MenuItem'
import FormHelperText from '@mui/material/FormHelperText'
import FormControl from '@mui/material/FormControl'
import Select from '@mui/material/Select'
import TextField from '@mui/material/TextField';
import Map, { Marker } from 'react-map-gl';
import PropTypes from 'prop-types';
import Box from '@mui/material/Box';
import Collapse from '@mui/material/Collapse';
import IconButton from '@mui/material/IconButton';
import Table from '@mui/material/Table';
import TableBody from '@mui/material/TableBody';
import TableCell from '@mui/material/TableCell';
import TableContainer from '@mui/material/TableContainer';
import TableHead from '@mui/material/TableHead';
import TableRow from '@mui/material/TableRow';
import Typography from '@mui/material/Typography';
import Paper from '@mui/material/Paper';
import KeyboardArrowDownIcon from '@mui/icons-material/KeyboardArrowDown';
import KeyboardArrowUpIcon from '@mui/icons-material/KeyboardArrowUp';
import { valueToPercent } from '@mui/base';
import '../Assets/Css/SearchBar.css'

export default function Home() {
  const [kecamatan, setKecamatan] = React.useState('')
  const [dataKecamatan, setdataKecamatan] = React.useState('')
  const [jalan, setJalan] = React.useState('')
  const [dataJalan, setDataJalan] = React.useState('')
  const [error, setError] = useState(null)
  const [success, setSuccess] = useState(null)
  const [loading, setLoading] = useState(false);

  const handleChangeKecamatan = (event) => {
    setKecamatan(event.target.value)
    if (event.target.value === 'pilih') {
      setKecamatan('')
    }
  }

  const handleChangeJalan = (event) => {
    setJalan(event.target.value)
    console.log(event.type)
    // if (event.type === "mousedown") {
    //     console.log("Down")
    // } 
    // else if (event.type === "mouseup") {
    //   console.log("Up")
    // }
    // else if (event.type === "mouseleave") {
    //   console.log("Mouse Leave")
    //   // setDataJalan('')
    // }
    // else if (event.type === "change") {
    //   console.log(event.type)
    // }
    // else {

    // }
  }

  const kecamatanLoadValue = async (value) => {
    const url = value.target.dataset.value
    if (url === 'pilih') {
      setdataKecamatan(value)
    }
    const result = await axios
      .get(`http://localhost:5000/home/${url}`)
      .catch((err) => {
        if (err) {
          setError(err)
          setSuccess(null)
        }
      })
    if (result) {
      setError(null);
      setDataJalan(result)
      console.log(dataJalan)
    }
  }

  const searchLoadValue = async (value) => {
    try {
      const url = value.target.value
      const result = await fetch(`http://localhost:5000/home/search/${url}`)
      const data = await result.json()
  
      if (data.length > 0) {
        setDataJalan(data)
        console.log(dataJalan)
      }

    } catch (error) {
      console.error(error.message)
      setDataJalan([])
    }
  }

  // const datas = []

  // dataJalan.forEach((data) => {
  //   datas.push(<li>{data}</li>)
  // })

  // const searchLoadValue = async (value) => {
  //   const url = value.target.value
  //   const result = await axios
  //     .get(`http://localhost:5000/home/search/${url}`)
  //     .catch((err) => {
  //       if (err) {
  //         setError(err)
  //         setSuccess(null)
  //       }
  //     })
  //   if (data) {
  //     setError(null);
  //     setDataJalan(data)
  //   }
  // }

  // const dataJson = JSON.stringify(dataJalan.data)
  // console.log(JSON.stringify(dataJalan.data))

  // const dataResult = dataJson.map((value) => value.nama_ruas)
  // console.log(dataJalan)



  return (
    <>
      <div style={{ display: "flex", justifyContent: "center", alignItems: "center", paddingTop: "1.5rem" }}>
        <Box>
          <FormControl sx={{ m: 1, minWidth: 200, justifyContent: "center", alignContent: "center" }}>
            <InputLabel id="demo-simple-select-helper-label">Kecamatan</InputLabel>
            <Select
              labelId="demo-simple-select-helper-label"
              id="demo-simple-select-helper"
              label="Kecamatan"
              value={kecamatan}
              onChange={handleChangeKecamatan}
            >
              <MenuItem value="pilih" onClick={kecamatanLoadValue}>
                <em>---Pilih---</em>
              </MenuItem>
              <MenuItem value={'kec-telanaipura'} onClick={kecamatanLoadValue}>Kec. Telanaipura</MenuItem>
              <MenuItem value={'kec-palmerah'} onClick={kecamatanLoadValue}>Kec. Palmerah</MenuItem>
              <MenuItem value={'kec-jambiselatan'} onClick={kecamatanLoadValue}>Kec. Jambi Selatan</MenuItem>
              <MenuItem value={'kec-jambitimur'} onClick={kecamatanLoadValue}>Kec. Jambi Timur</MenuItem>
              <MenuItem value={'kec-pelayangan'} onClick={kecamatanLoadValue}>Kec. Pelayangan</MenuItem>
              <MenuItem value={'kec-pasarjambi'} onClick={kecamatanLoadValue}>Kec. Pasar Jambi</MenuItem>
              <MenuItem value={'kec-danauteluk'} onClick={kecamatanLoadValue}>Kec. Danau Teluk</MenuItem>
              <MenuItem value={'kec-kotabaru'} onClick={kecamatanLoadValue}>Kec. Kota Baru</MenuItem>
              <MenuItem value={'kec-jelutung'} onClick={kecamatanLoadValue}>Kec. Jelutung</MenuItem>
              <MenuItem value={'kec-alambarajo'} onClick={kecamatanLoadValue}>Kec. Alam Barajo</MenuItem>
              <MenuItem value={'kec-danausipin'} onClick={kecamatanLoadValue}>Kec. Danau Sipin</MenuItem>
            </Select>
            <FormHelperText>Pilih Kecamatan</FormHelperText>
          </FormControl>
          <FormControl sx={{ m: 1, minWidth: 100 }}>
            <TextField
              label="Jalan.."
              id="outlined-basic"
              variant="outlined"
              value={jalan}
              onChange={handleChangeJalan}
              onInput={searchLoadValue}
              onMouseDown={handleChangeJalan}
              onMouseUp={handleChangeJalan}
              onMouseLeave={handleChangeJalan}
              onAbort={handleChangeJalan}
              onCut={handleChangeJalan}
            >
            </TextField>
            <FormHelperText>Tulis Jalan</FormHelperText>
            {dataJalan.length != 0 && (
            <div className="dataResult">
              {Array.from(dataJalan).map(value => {
                return (
                  <a className="dataItem" key={value.no} target="_blank">
                    <p>{value.jl}{value.nama_ruas}</p>
                  </a>
                )
              })}
            </div>
            )}
          </FormControl>
        </Box>

      </div>
      <div style={{ margin: '10px 50px 40px 50px', height: '68vh', width: '134vh', display: 'flex', justifyContent: 'center', alignContent: 'center', alignItems: 'center', border: 'solid black 1px', borderRadius: '5px', background: "#756859" }}>
        <Map
          mapboxAccessToken={"pk.eyJ1IjoiamFpcnVzY2FsdmluIiwiYSI6ImNsNWtrbTZqMzBiMnMzb3BqMDI2bHlxb2IifQ.6t_OCZJV4hpBJo1PgJPnfA"}
          initialViewState={{
            longitude: 103.607254,
            latitude: -1.609972,
            zoom: 11.5,
            pitch: 50
          }}
          style={{ width: 1000, height: 500, border: 'solid black 1px', borderRadius: '5px' }}
          mapStyle="mapbox://styles/mapbox/streets-v9"
        >
          <Marker
            longitude={103.607254}
            latitude={-1.609972}
            offsetTop={{ 40: 2 }}>
            <img
              src="https://png.pngtree.com/png-vector/20210303/ourmid/pngtree-maps-pointer-with-circle-shadow-png-image_2983412.jpg"
              width={8 * 5}
              height={8 * 5}
            />
          </Marker>
        </Map>
      </div>
      <hr></hr>

      <div>
        {/* {dataJalan}
        <ul>
          {datas}
        </ul> */}

      {/* {Array.from(dataJalan).map(element => {
        return (
            <ul type="disc" key={element.no}>
                <li style={{ 
                    fontWeight: 'bold', 
                    color: 'red' }}
                >
                    {element.no_ruas}
                </li>
                <li>{element.nama_ruas}</li>
            </ul>
        )
      })} */}

      {/* <TableContainer component={Paper}>
        <Table aria-label="customized table">
        <TableHead>
          <TableRow>
            <TableCell>No</TableCell>
            <TableCell>Nama Jalan</TableCell>
            <TableCell>Kecamatan Yang Dilalui</TableCell>
          </TableRow>
        </TableHead>
        <TableBody>
        {Array.from(dataJalan).map(element => {
          return ( 
            <TableRow key={element.no}>
              <TableCell>{element.no_ruas}</TableCell>
              <TableCell>{element.jl}{element.nama_ruas}</TableCell>
              <TableCell>{element.kecamatan_yang_dilalui}</TableCell>
            </TableRow>
          )
        })}
        </TableBody>
        </Table>
      </TableContainer> */}
     
        {/* <table>  
            <tr>  
                <th style={{width:"300px"}}>No</th>  
                <th style={{width:"400px"}}>Jalan</th>  
                <th style={{width:"400px"}}>Kecamatan</th>  
            </tr>  
    
            {dataJalan.map((data, index) => (  
              <tr data-index={index}>  
                <td>{data.no_ruas}</td>  
                <td>{data.nama_ruas}</td>  
                <td>{data.kecamatan_yang_dilalui}</td>  
              </tr>  
            ))}  
    
        </table>  */}
      </div>
    </>
  )
}
