import * as React from 'react'
import axios from 'axios';
import { useState } from 'react'
import { useFormik } from 'formik'
import * as yup from 'yup';
import InputLabel from '@mui/material/InputLabel'
import MenuItem from '@mui/material/MenuItem'
import FormHelperText from '@mui/material/FormHelperText'
import FormControl from '@mui/material/FormControl'
import Select from '@mui/material/Select'
import TextField from '@mui/material/TextField';
import Map, { Marker } from 'react-map-gl';
import PropTypes from 'prop-types';
import Box from '@mui/material/Box';
import Collapse from '@mui/material/Collapse';
import IconButton from '@mui/material/IconButton';
import Table from '@mui/material/Table';
import TableBody from '@mui/material/TableBody';
import TableCell from '@mui/material/TableCell';
import TableContainer from '@mui/material/TableContainer';
import TableHead from '@mui/material/TableHead';
import TableRow from '@mui/material/TableRow';
import Typography from '@mui/material/Typography';
import Paper from '@mui/material/Paper';
import KeyboardArrowDownIcon from '@mui/icons-material/KeyboardArrowDown';
import KeyboardArrowUpIcon from '@mui/icons-material/KeyboardArrowUp';
import { valueToPercent } from '@mui/base';
import '../Assets/Css/SearchBar.css'
import '../Assets/Css/homePages.css'
import { useEffect } from 'react';

export default function Home() {
  const [kecamatan, setKecamatan] = React.useState('')
  const [jalan, setJalan] = React.useState('')
  const [filtered, setFiltered] = React.useState('')
  const [filterKecamatan, setFilterKecamatan] = React.useState('')
  const [allData, setAllData] = React.useState('')
  const [resultData, setResultData] = React.useState('')
  const [error, setError] = useState(null)
  const [success, setSuccess] = useState(null)
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    getAll()
  }, [])

  const handleChangeKecamatan = async (event) => {
    if (event.target.value === 'pilih') {
      setKecamatan('')
      getAll()
    } else {
      // const filteredKecamatan = allData.filter(data => {
      //   return data.kecamatan_yang_dilalui === event
      // })
      const result = await fetch(`http://localhost:5000/home/kec-${event.target.value}`)
      const data = await result.json()
      setKecamatan(event.target.value)
      setAllData(data)
    }
  }

  const selectValue = (event) => {
    const filltered = resultData.filter(data => {
      return data.nama_ruas == event
    })
    setFiltered(filltered)
  }

  const blur = (event) => {
    if (event.type === "blur") {
      setKecamatan('')
      setAllData('')
    }
  }

  const leave = (event) => {
    console.log(event.type)
    if (event.type === "mouseleave") {
      setJalan('')
      setResultData('')
    }
  }

  const searchDouble = async (event) => {
    setJalan(event.target.value)
    try {
      if (kecamatan.length === 0 && event.target.value.length === 0) {
        console.log('dataKecamatan dan dataJalan tidak ada')
      } else {
        const kec = kecamatan
        // console.log('dataKecamatan : ' + kec)
        // console.log('dataJalan : ' + event.target.value)

        const result = await fetch(`http://localhost:5000/home/search/${kec}/${event.target.value}`)
        const data = await result.json()

        if (data.length > 0) {
          setResultData(data)
        } else {
          setResultData('')
        }
      }
    } catch (error) {
      console.log(error)
    }
  }

  const getAll = async () => {
    try {
      const getData = await fetch(`http://localhost:5000/home/`)
      const data = await getData.json()

      if (data.length > 0) {
        setAllData(data)
      }

    } catch (error) {
      console.log(error)
    }
  }

  return (
    <>
      <div className="bg"></div>
      <div className="container">
        <div className="body">
          <Box>
            <FormControl sx={{ m: 1, minWidth: 200, justifyContent: "center", alignContent: "center" }}>
              <InputLabel id="demo-simple-select-helper-label">Kecamatan</InputLabel>
              <Select
                labelId="demo-simple-select-helper-label"
                id="demo-simple-select-helper"
                label="Kecamatan"
                value={kecamatan}
                onChange={handleChangeKecamatan}
                // onBlur={blur}
              >
                <MenuItem name="pilih" value="pilih" onClick={handleChangeKecamatan}>
                  <em>---Pilih---</em>
                </MenuItem>
                <MenuItem name="Kec. Telanaipura" value={'telanaipura'} onClick={handleChangeKecamatan}>Kec. Telanaipura</MenuItem>
                <MenuItem name="Kec. Palmerah" value={'palmerah'} onClick={handleChangeKecamatan}>Kec. Palmerah</MenuItem>
                <MenuItem name="Kec. Jambi Selatan" value={'jambiselatan'} onClick={handleChangeKecamatan}>Kec. Jambi Selatan</MenuItem>
                <MenuItem name="Kec. Jambi Timur" value={'jambitimur'} onClick={handleChangeKecamatan}>Kec. Jambi Timur</MenuItem>
                <MenuItem name="Kec. Pelayangan" value={'pelayangan'} onClick={handleChangeKecamatan}>Kec. Pelayangan</MenuItem>
                <MenuItem name="Kec. Pasar Jambi" value={'pasarjambi'} onClick={handleChangeKecamatan}>Kec. Pasar Jambi</MenuItem>
                <MenuItem name="Kec. Danau Teluk" value={'danauteluk'} onClick={handleChangeKecamatan}>Kec. Danau Teluk</MenuItem>
                <MenuItem name="Kec. Kota Baru" value={'kotabaru'} onClick={handleChangeKecamatan}>Kec. Kota Baru</MenuItem>
                <MenuItem name="Kec. Jelutung" value={'jelutung'} onClick={handleChangeKecamatan}>Kec. Jelutung</MenuItem>
                <MenuItem name="Kec. Alam Barajo" value={'alambarajo'} onClick={handleChangeKecamatan}>Kec. Alam Barajo</MenuItem>
                <MenuItem name="Kec. Danau Sipin" value={'danausipin'} onClick={handleChangeKecamatan}>Kec. Danau Sipin</MenuItem>
              </Select>
              <FormHelperText>Pilih Kecamatan</FormHelperText>
            </FormControl>
            <FormControl sx={{ m: 1, minWidth: 100 }}>
              <TextField
                label="Jalan.."
                id="outlined-basic"
                variant="outlined"
                value={jalan}
                onInput={searchDouble}
                onChange={searchDouble}
              // onBlur={blur}
              >
              </TextField>
              {resultData.length <= 1 && (
                <FormHelperText>Tulis Jalan</FormHelperText>
              )}
              {resultData.length >= 0 && (
                <div className="dataResult" onMouseLeave={leave}>
                  {Array.from(resultData).map(value => {
                    return (
                      <a className="dataItem" key={value.no} target="_blank">
                        <p
                          onClick={() => selectValue(value.nama_ruas)}
                        >
                          Jl.{value.nama_ruas}, Kecamatan {value.kecamatan_yang_dilalui}
                        </p>
                      </a>
                    )
                  })}
                </div>
              )}
            </FormControl>
          </Box>
        </div>

        {/* <div style={{ margin: '10px 50px 40px 50px', height: '68vh', width: '134vh', display: 'flex', justifyContent: 'center', alignContent: 'center', alignItems: 'center', border: 'solid black 1px', borderRadius: '5px', background: "#756859" }}>
          <Map
            mapboxAccessToken={"pk.eyJ1IjoiamFpcnVzY2FsdmluIiwiYSI6ImNsNWtrbTZqMzBiMnMzb3BqMDI2bHlxb2IifQ.6t_OCZJV4hpBJo1PgJPnfA"}
            initialViewState={{
              longitude: 103.607254,
              latitude: -1.609972,
              zoom: 11.5,
              pitch: 50
            }}
            style={{ width: 1000, height: 500, border: 'solid black 1px', borderRadius: '5px' }}
            mapStyle="mapbox://styles/mapbox/streets-v9"
          >
            <Marker
              longitude={103.607254}
              latitude={-1.609972}
              offsetTop={{ 40: 2 }}>
              <img
                src="https://png.pngtree.com/png-vector/20210303/ourmid/pngtree-maps-pointer-with-circle-shadow-png-image_2983412.jpg"
                width={8 * 5}
                height={8 * 5}
              />
            </Marker>
          </Map>
        </div>
        <hr></hr> */}

        <div style={{ padding: "20px 50px" }}>
          <TableContainer component={Paper}>
            <Table aria-label="customized table">
              <TableHead>
                <TableRow>
                  <TableCell>No Ruas</TableCell>
                  <TableCell>Nama Jalan</TableCell>
                  <TableCell>Kecamatan Yang Dilalui</TableCell>
                </TableRow>
              </TableHead>
              {allData.length === 0 ? (
                <TableBody>
                <TableRow>
                  <TableCell></TableCell>
                </TableRow>
                </TableBody>
              ) : ( 
                allData.map(element => (
                    <TableBody>
                    <TableRow key={element.no}>
                      <TableCell>{element.no_ruas}</TableCell>
                      <TableCell>{element.jl}{element.nama_ruas}</TableCell>
                      <TableCell>{element.kecamatan_yang_dilalui}</TableCell>
                    </TableRow>
                    </TableBody>
                ))
              )}
            </Table>
          </TableContainer>
        </div>
      </div>
    </>
  )
}
