import '../Assets/Css/SearchBar.css'

export default function Home() {
  const [kecamatan, setKecamatan] = React.useState('')
  const [dataKecamatan, setdataKecamatan] = React.useState('')
  const [jalan, setJalan] = React.useState('')
  const [dataJalan, setDataJalan] = React.useState('')
  const [error, setError] = useState(null)
  const [success, setSuccess] = useState(null)
  const [loading, setLoading] = useState(false);

  const handleChangeKecamatan = (event) => {
    setKecamatan(event.target.value)
    if (event.target.value === 'pilih') {
      setKecamatan('')
    }
  }

  const handleChangeJalan = (event) => {
    setJalan(event.target.value)
  }

  const kecamatanLoadValue = async (value) => {
    const url = value.target.dataset.value
    if (url === 'pilih') {
      setdataKecamatan(value)
    }
    const result = await axios
      .get(`http://localhost:5000/home/${url}`)
      .catch((err) => {
        if (err) {
          setError(err)
          setSuccess(null)
        }
      })
    if (result) {
      setError(null);
      setDataJalan(result)
      console.log(dataJalan)
    }
  }

  const searchLoadValue = async (value) => {
    try {
      const url = value.target.value
      const result = await fetch(`http://localhost:5000/home/search/${url}`)
      const data = await result.json()
  
      if (data.length > 0) {
  
        setDataJalan(data)
  
        // console.log(data)
        console.log(dataJalan)
      }

    } catch (error) {
      console.error(error.message)
      setDataJalan('')
    }
  }

