import * as React from 'react'
import InputLabel from '@mui/material/InputLabel'
import MenuItem from '@mui/material/MenuItem'
import FormHelperText from '@mui/material/FormHelperText'
import FormControl from '@mui/material/FormControl'
import Select from '@mui/material/Select'
import { useFormik } from 'formik'
import * as yup from 'yup';
import axios from 'axios';
import { useState } from 'react'
import Box from '@mui/material/Box';
import TextField from '@mui/material/TextField';

export default function Home() {
  const [kecamatan, setKecamatan] = React.useState('')
  const [dataKecamatan, setdataKecamatan] = React.useState('')
  const [jalan, setJalan] = React.useState('')
  const [error, setError] = useState(null)
  const [success, setSuccess] = useState(null)

  const handleChangeKecamatan = (event) => {
    setKecamatan(event.target.value)
    if (event.target.value === 'pilih') {
      setKecamatan('')
    }
  }

  const handleChangeJalan = (event) => {
    setJalan(event.target.value)
  }

  const handleLoadValue = async (value) => {
    const url = value.target.dataset.value
    if (url === 'pilih') {
      setdataKecamatan(value)
    }
    const result = await axios
      .get(`http://localhost:5000/home/${url}`)
      .catch((err) => {
        if (err) {
          setError(err)
          setSuccess(null)
        }
      })
    if (result) {
      setError(null);
      setdataKecamatan(result)
    }
  }

  return (
    <div style={{display:"flex", justifyContent:"center"}}>
      <Box>
        <FormControl sx={{ m: 1, minWidth: 200, justifyContent: "center", alignContent:"center" }}>
          <InputLabel id="demo-simple-select-helper-label">Kecamatan</InputLabel>
          <Select
            labelId="demo-simple-select-helper-label"
            id="demo-simple-select-helper"
            label="Kecamatan"
            value={kecamatan}
            onChange={handleChangeKecamatan}
          >
            <MenuItem value="pilih" onClick={handleLoadValue}>
              <em>---Pilih---</em>
            </MenuItem>
            <MenuItem value={'kec-telanaipura'} onClick={handleLoadValue}>Kec. Telanaipura</MenuItem>
            <MenuItem value={'kec-palmerah'} onClick={handleLoadValue}>Kec. Palmerah</MenuItem>
            <MenuItem value={'kec-jambiselatan'} onClick={handleLoadValue}>Kec. Jambi Selatan</MenuItem>
            <MenuItem value={'kec-jambitimur'} onClick={handleLoadValue}>Kec. Jambi Timur</MenuItem>
            <MenuItem value={'kec-pelayangan'} onClick={handleLoadValue}>Kec. Pelayangan</MenuItem>
            <MenuItem value={'kec-pasarjambi'} onClick={handleLoadValue}>Kec. Pasar Jambi</MenuItem>
            <MenuItem value={'kec-danauteluk'} onClick={handleLoadValue}>Kec. Danau Teluk</MenuItem>
            <MenuItem value={'kec-kotabaru'} onClick={handleLoadValue}>Kec. Kota Baru</MenuItem>
            <MenuItem value={'kec-jelutung'} onClick={handleLoadValue}>Kec. Jelutung</MenuItem>
            <MenuItem value={'kec-alambarajo'} onClick={handleLoadValue}>Kec. Alam Barajo</MenuItem>
            <MenuItem value={'kec-danausipin'} onClick={handleLoadValue}>Kec. Danau Sipin</MenuItem>
          </Select>
          <FormHelperText>Pilih Kecamatan</FormHelperText>
        </FormControl>
        <FormControl sx={{ m: 1, minWidth: 100 }}>
          {/* {JSON.stringify(dataKecamatan.data)} */}
          <TextField
            label="Jalan.."
            id="outlined-basic"
            variant="outlined"
            value={jalan}
            onChange={handleChangeJalan}
          >
          </TextField>
          <FormHelperText>Ketikan Ruas Jalan</FormHelperText>
        </FormControl>
      </Box>

    </div>
  )
}
