import * as React from 'react'
import axios from 'axios';
import { useState } from 'react'
import { useFormik } from 'formik'
import * as yup from 'yup';
import InputLabel from '@mui/material/InputLabel'
import MenuItem from '@mui/material/MenuItem'
import FormHelperText from '@mui/material/FormHelperText'
import FormControl from '@mui/material/FormControl'
import Select from '@mui/material/Select'
import TextField from '@mui/material/TextField';
import Map, { Marker } from 'react-map-gl';
import PropTypes from 'prop-types';
import Box from '@mui/material/Box';
import Collapse from '@mui/material/Collapse';
import IconButton from '@mui/material/IconButton';
import Table from '@mui/material/Table';
import TableBody from '@mui/material/TableBody';
import TableCell from '@mui/material/TableCell';
import TableContainer from '@mui/material/TableContainer';
import TableHead from '@mui/material/TableHead';
import TableRow from '@mui/material/TableRow';
import Typography from '@mui/material/Typography';
import Paper from '@mui/material/Paper';
import KeyboardArrowDownIcon from '@mui/icons-material/KeyboardArrowDown';
import KeyboardArrowUpIcon from '@mui/icons-material/KeyboardArrowUp';
import { valueToPercent } from '@mui/base';
import '../Assets/Css/SearchBar.css'
import '../Assets/Css/homePages.css'

export default function Home() {
  const [kecamatan, setKecamatan] = React.useState('')
  const [jalan, setJalan] = React.useState('')
  const [newJalan, setNewJalan] = React.useState('')
  const [selectedJalan, setSelectedJalan] = React.useState('')
  const [resultData, setResultData] = React.useState('')
  const [error, setError] = useState(null)
  const [success, setSuccess] = useState(null)
  const [loading, setLoading] = useState(false);

  const handleChangeKecamatan = (event) => {
    setKecamatan(event.target.value)
    // setdataKecamatan(event.target.value)
    if (event.target.value === 'pilih') {
      setKecamatan('')
      // setdataKecamatan('')
    }
  }

  const selectValue = async (event) => {
    setNewJalan(event)
    if (event.length > 0 && kecamatan.length > 0) {
      const kec = kecamatan
      const result = await fetch(`http://localhost:5000/home/search/${kec}/${event}`)
      const data = await result.json()

      if (data.length > 0) {
        setSelectedJalan(data)
      } else {
        setSelectedJalan('')
      }
    } else {
      console.log('tidak ada')
    }
  }

  const blur = (event) => {
    if (event.type === "blur") {
      setJalan('')
      setResultData('')
    }
  }

  // const kecamatanLoadValue = async (value) => {
  //   const url = value.target.dataset.value
  //   if (url === 'pilih') {
  //     setdataKecamatan(value)
  //   }
  //   const result = await axios
  //     .get(`http://localhost:5000/home/${url}`)
  //     .catch((err) => {
  //       if (err) {
  //         setError(err)
  //         setSuccess(null)
  //       }
  //     })
  //   if (result) {
  //     setError(null);
  //     setDataJalan(result)
  //   }
  // }

  // const searchLoadValue = async (value) => {
  //   try {
  //     const url = value.target.value
  //     const result = await fetch(`http://localhost:5000/home/search/${url}`)
  //     const data = await result.json()

  //     if (data.length > 0) {
  //       setDataJalan(data)
  //       // console.log(dataJalan)
  //     } else {
  //       setDataJalan('')
  //     }

  //   } catch (error) {
  //     console.error(error.message)
  //     setDataJalan('')
  //   }
  // }

  const searchDouble = async (event) => {
    setJalan(event.target.value)
    // console.log(event.type)
    // console.log(event.target.value)

    try {
      if (kecamatan.length === 0 && event.target.value.length === 0) {
        console.log('dataKecamatan dan dataJalan tidak ada')
      } else {
        const kec = kecamatan
        // console.log('dataKecamatan : ' + kec)
        // console.log('dataJalan : ' + event.target.value)

        const result = await fetch(`http://localhost:5000/home/search/${kec}/${event.target.value}`)
        const data = await result.json()

        if (data.length > 0) {
          setResultData(data)
        } else {
          setResultData('')
        }

      }
      
      console.log(newJalan)
      if (newJalan > 0) {
        setJalan('event')
      }else {
        console.log('')
      }
    } catch (error) {
      console.log(error)
    }


  }

  return (
    <>
      <div className="bg"></div>
      <div className="container">
        <div className="body">
          <Box>
            <FormControl sx={{ m: 1, minWidth: 200, justifyContent: "center", alignContent: "center" }}>
              <InputLabel id="demo-simple-select-helper-label">Kecamatan</InputLabel>
              <Select
                labelId="demo-simple-select-helper-label"
                id="demo-simple-select-helper"
                label="Kecamatan"
                value={kecamatan}
                onChange={handleChangeKecamatan}
              >
                <MenuItem value="pilih" onChange={searchDouble}>
                  <em>---Pilih---</em>
                </MenuItem>
                <MenuItem value={'Telanaipura'} onChange={searchDouble}>Kec. Telanaipura</MenuItem>
                <MenuItem value={'Palmerah'} onChange={searchDouble}>Kec. Palmerah</MenuItem>
                <MenuItem value={'Jambi Selatan'} onChange={searchDouble}>Kec. Jambi Selatan</MenuItem>
                <MenuItem value={'Jambi Timur'} onChange={searchDouble}>Kec. Jambi Timur</MenuItem>
                <MenuItem value={'Pelayangan'} onChange={searchDouble}>Kec. Pelayangan</MenuItem>
                <MenuItem value={'Pasar Jambi'} onChange={searchDouble}>Kec. Pasar Jambi</MenuItem>
                <MenuItem value={'Danau Teluk'} onChange={searchDouble}>Kec. Danau Teluk</MenuItem>
                <MenuItem value={'Kota Baru'} onChange={searchDouble}>Kec. Kota Baru</MenuItem>
                <MenuItem value={'Jelutung'} onChange={searchDouble}>Kec. Jelutung</MenuItem>
                <MenuItem value={'Alam Barajo'} onChange={searchDouble}>Kec. Alam Barajo</MenuItem>
                <MenuItem value={'Danau Sipin'} onChange={searchDouble}>Kec. Danau Sipin</MenuItem>
              </Select>
              <FormHelperText>Pilih Kecamatan</FormHelperText>
            </FormControl>
            <FormControl sx={{ m: 1, minWidth: 100 }}>
              <TextField
                label="Jalan.."
                id="outlined-basic"
                variant="outlined"
                value={jalan}
                onInput={searchDouble}
                // onChange={searchDouble}
                // onBlur={blur}
              >
              </TextField>
              {resultData.length === 0 && (
                <FormHelperText>Tulis Jalan</FormHelperText>
              )}
              {resultData.length !== 0 && (
                <div className="dataResult">
                  {Array.from(resultData).map(value => {
                    return (
                      <a className="dataItem" key={value.no} target="_blank">
                        <p
                          onClick={() => selectValue(value.nama_ruas)}
                        >
                          Jl.{value.nama_ruas}, Kecamatan{value.kecamatan_yang_dilalui}
                        </p>
                      </a>
                    )
                  })}
                </div>
              )}
            </FormControl>
          </Box>
        </div>

        {/* <div style={{ margin: '10px 50px 40px 50px', height: '68vh', width: '134vh', display: 'flex', justifyContent: 'center', alignContent: 'center', alignItems: 'center', border: 'solid black 1px', borderRadius: '5px', background: "#756859" }}>
          <Map
            mapboxAccessToken={"pk.eyJ1IjoiamFpcnVzY2FsdmluIiwiYSI6ImNsNWtrbTZqMzBiMnMzb3BqMDI2bHlxb2IifQ.6t_OCZJV4hpBJo1PgJPnfA"}
            initialViewState={{
              longitude: 103.607254,
              latitude: -1.609972,
              zoom: 11.5,
              pitch: 50
            }}
            style={{ width: 1000, height: 500, border: 'solid black 1px', borderRadius: '5px' }}
            mapStyle="mapbox://styles/mapbox/streets-v9"
          >
            <Marker
              longitude={103.607254}
              latitude={-1.609972}
              offsetTop={{ 40: 2 }}>
              <img
                src="https://png.pngtree.com/png-vector/20210303/ourmid/pngtree-maps-pointer-with-circle-shadow-png-image_2983412.jpg"
                width={8 * 5}
                height={8 * 5}
              />
            </Marker>
          </Map>
        </div>
        <hr></hr> */}



        {selectedJalan !== 0 && (
          <div>
            <TableContainer component={Paper}>
              <Table aria-label="customized table">
                <TableHead>
                  <TableRow>
                    <TableCell>No</TableCell>
                    <TableCell>Nama Jalan</TableCell>
                    <TableCell>Kecamatan Yang Dilalui</TableCell>
                  </TableRow>
                </TableHead>
                <TableBody>
                  {Array.from(selectedJalan).map(element => {
                    return (
                      <TableRow key={element.no}>
                        <TableCell>{element.no_ruas}</TableCell>
                        <TableCell>{element.jl}{element.nama_ruas}</TableCell>
                        <TableCell>{element.kecamatan_yang_dilalui}</TableCell>
                      </TableRow>
                    )
                  })}
                </TableBody>
              </Table>
