import * as React from 'react'
import axios from 'axios';
import { useState } from 'react'
import { useFormik } from 'formik'
import * as yup from 'yup';
import InputLabel from '@mui/material/InputLabel'
import MenuItem from '@mui/material/MenuItem'
import FormHelperText from '@mui/material/FormHelperText'
import FormControl from '@mui/material/FormControl'
import Select from '@mui/material/Select'
import TextField from '@mui/material/TextField';
import Map, { Marker } from 'react-map-gl';
import PropTypes from 'prop-types';
import Box from '@mui/material/Box';
import Collapse from '@mui/material/Collapse';
import IconButton from '@mui/material/IconButton';
import Table from '@mui/material/Table';
import TableBody from '@mui/material/TableBody';
import TableCell from '@mui/material/TableCell';
import TableContainer from '@mui/material/TableContainer';
import TableHead from '@mui/material/TableHead';
import TableRow from '@mui/material/TableRow';
import Typography from '@mui/material/Typography';
import Paper from '@mui/material/Paper';
import VisibilityIcon from '@mui/icons-material/Visibility';
import Tooltip from '@mui/material/Tooltip';
import KeyboardArrowDownIcon from '@mui/icons-material/KeyboardArrowDown';
import KeyboardArrowUpIcon from '@mui/icons-material/KeyboardArrowUp';
import { valueToPercent } from '@mui/base';
import '../Assets/Css/SearchBar.css'
import '../Assets/Css/homePages.css'
import { useEffect } from 'react';
import { Button } from '@mui/material';
import Modal from '@mui/material/Modal';
import ClipLoader from "react-spinners/ClipLoader";
import Checkbox from '@mui/material/Checkbox';
import Pagination from '@mui/material/Pagination';
import Stack from '@mui/material/Stack';

export default function Home() {
  const [kecamatan, setKecamatan] = React.useState('')
  const [jalan, setJalan] = React.useState('')
  const [allData, setAllData] = React.useState('')
  const [resultData, setResultData] = React.useState('')
  const [error, setError] = useState(null)
  const [success, setSuccess] = useState(null)
  const [loading, setLoading] = useState(false);
  const [open, setOpen] = React.useState(false);

  const handleOpen = () => {
    setOpen(true);
  };
  const handleClose = () => {
    setOpen(false);
  };

  const handleChangeKecamatan = async (event) => {
    setLoading(true)
    if (event.target.value === 'pilih') {
      setKecamatan('')
      getAll()
    } else {
      const result = await fetch(`http://localhost:5000/home/kec-${event.target.value}`)
      const data = await result.json()
      setKecamatan(event.target.value)
      setAllData(data)
    }
    setLoading(false)
  }

  const handleRuasJalan = async (event) => {
    setLoading(true)
    if (kecamatan.length > 0 && event.target.value === 0) {
      getAll()
    } else {
      try {
        setJalan(event.target.value)
        const kec = kecamatan
        const result = await fetch(`http://localhost:5000/home/search/${kec}/${event.target.value}`)
        const data = await result.json()
        setAllData(data)
      } catch (error) {
        console.log(error)
      }
    }
    setLoading(false)
  }

  const handleReset = () => {
    setLoading(true)
    setKecamatan('')
    setJalan('')
    getAll()
    setLoading(false)
  }

  const selectValue = (event) => {
    setJalan(event.target.value)
    const filltered = allData.filter(data => {
      return data.nama_ruas == event.target.value.toLowerCase()
    })
    console.log(jalan)
    setAllData(filltered)

    if (jalan.length < 0) {
      console.log('kosong')
    }
  }

  const blur = (event) => {
    if (event.type === "blur") {
      setKecamatan('')
      setAllData('')
    }
  }

  const leave = (event) => {
    console.log(event.type)
    if (event.type === "mouseleave") {
      setJalan('')
      setResultData('')
    }
  }

  const getAll = async () => {
    // setLoading(true)
    try {
      const getData = await fetch(`http://localhost:5000/home/`)
      const data = await getData.json()

      if (data.length > 0) {
        setAllData(data)
      }

    } catch (error) {
      console.log(error)
    }
    // setLoading(false)
  }

  const label = { inputProps: { 'aria-label': 'Checkbox demo' } };

  useEffect(() => {
    setLoading(true)
    getAll()
    
    setTimeout(() => {
      setLoading(false)
    }, 10000)
  }, [])

  return (
    <>
      <div className="bg"></div>
      <div className="container">
        <div className="body">
          <Box>
            <FormControl sx={{ m: 1, minWidth: 200, justifyContent: "center", alignContent: "center" }}>
              <InputLabel id="demo-simple-select-helper-label">Kecamatan</InputLabel>
              <Select
                labelId="demo-simple-select-helper-label"
                id="demo-simple-select-helper"
                label="Kecamatan"
                value={kecamatan}
                onChange={handleChangeKecamatan}
              >
                <MenuItem value="pilih" onClick={handleChangeKecamatan}>
                  <em>---Pilih---</em>
                </MenuItem>
                <MenuItem value={'Telanaipura'} onClick={handleChangeKecamatan}>Kec. Telanaipura</MenuItem>
                <MenuItem value={'Palmerah'} onClick={handleChangeKecamatan}>Kec. Palmerah</MenuItem>
                <MenuItem value={'Jambi Selatan'} onClick={handleChangeKecamatan}>Kec. Jambi Selatan</MenuItem>
                <MenuItem value={'Jambi Timur'} onClick={handleChangeKecamatan}>Kec. Jambi Timur</MenuItem>
                <MenuItem value={'Pelayangan'} onClick={handleChangeKecamatan}>Kec. Pelayangan</MenuItem>
